var swiper=new Swiper(".results__slides",{slidesPerView:2,loop:!0,pagination:{el:".swiper-pagination",type:"fraction"},navigation:{nextEl:".results__slider-btn--next",prevEl:".results__slider-btn--prev"}});document.querySelectorAll(".questions__item").forEach(e=>{e.addEventListener("click",()=>{e.classList.add("questions__item-active");let t=e.querySelector(".questions__item-text").scrollHeight,i=e.querySelector(".questions__item-text");i.style.maxHeight=t+"px",i.clientHeight==i.scrollHeight&&(i.style.maxHeight="0px",e.classList.remove("questions__item-active"))})});const checkNumInputs=e=>{document.querySelectorAll(e).forEach(e=>{e.addEventListener("input",()=>{e.value=e.value.replace(/[^\+\d]/,"")})})};checkNumInputs(".form__input-phone");